import React from 'react';
import Select from 'react-select';
import './Dropdown.css';

const currencies = [
  { label: 'USD', value: 'USD' },
  { label: 'EUR', value: 'EUR' },
  { label: 'BTC', value: 'BTC' },
  { label: 'JPY', value: 'JPY' },
  { label: 'GBP', value: 'GBP' },
  { label: 'AUD', value: 'AUD' },
  { label: 'CAD', value: 'CAD' },
  { label: 'CHF', value: 'CHF' },
  { label: 'SEK', value: 'SEK' },
  { label: 'NZD', value: 'NZD' },
  { label: 'KRW', value: 'KRW' },
  { label: 'CNY', value: 'CNY' },
  { label: 'SGD', value: 'SGD' },
  { label: 'NOK', value: 'NOK' },
  { label: 'MXN', value: 'MXN' },
  { label: 'INR', value: 'INR' },
  { label: 'RUB', value: 'RUB' },
  { label: 'ZAR', value: 'ZAR' },
  { label: 'TRY', value: 'TRY' },
  { label: 'BRL', value: 'BRL' },
  { label: 'TWD', value: 'TWD' },
  { label: 'DKK', value: 'DKK' },
  { label: 'PLN', value: 'PLN' },
  { label: 'THB', value: 'THB' },
  { label: 'IDR', value: 'IDR' },
  { label: 'HUF', value: 'HUF' },
  { label: 'CZK', value: 'CZK' },
  { label: 'ILS', value: 'ILS' },
  { label: 'CLP', value: 'CLP' },
  { label: 'PHP', value: 'PHP' },
  { label: 'AED', value: 'AED' },
  { label: 'COP', value: 'COP' },
  { label: 'SAR', value: 'SAR' },
  { label: 'RON', value: 'RON' },
  { label: 'HUF', value: 'HUF' },
  { label: 'CZK', value: 'CZK' },
  { label: 'ILS', value: 'ILS' },
  { label: 'CLP', value: 'CLP' },
  { label: 'PHP', value: 'PHP' },
  { label: 'AED', value: 'AED' },
  { label: 'COP', value: 'COP' },
  { label: 'SAR', value: 'SAR' },
  { label: 'MYR', value: 'MYR' },
];

function Dropdown() {
  const [options, setOptions] = useState(currencies);
  const [loading, setLoading] = useState(false);

  const fetchCurrencies = async (query) => {
    const data = query ? currencies.filter(currency => currency.label.toLowerCase().includes(query.toLowerCase())) : currencies;
    setOptions(data);
    setLoading(false);
  }

  return (
    <div id="dropdown">
      <Select
        options={options}
        isMulti
        hideSelectedOptions={false}
        id="select"
        placeholder={<div id="query">Search</div>}
        isLoading={loading}
        onInputChange={(query) => {fetchCurrencies(query)}}
      />
    </div>
  )
}

export default Dropdown;
